/* Content
----------------------------------------------- */
body {
	font: normal normal 15px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
	color: #333333;
	background: #111111
		url(//themes.googleusercontent.com/image?id=1OACCYOE0-eoTRTfsBuX1NMN9nz599ufI1Jh0CggPFA_sK80AGkIr8pLtYRpNUKPmwtEa)
		repeat-x fixed top center;
}

html body .region-inner {
	min-width: 0;
	max-width: 100%;
	width: auto;
}

.content-outer {
	font-size: 90%;
}

a:link {
	text-decoration: none;
	color: #336699;
}

a:visited {
	text-decoration: none;
	color: #6699cc;
}

a:hover {
	text-decoration: underline;
	color: #33aaff;
}

.content-outer {
	background: transparent none repeat scroll top left;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-goog-ms-border-radius: 0;
	border-radius: 0;
	-moz-box-shadow: 0 0 0 rgba(0, 0, 0, .15);
	-webkit-box-shadow: 0 0 0 rgba(0, 0, 0, .15);
	-goog-ms-box-shadow: 0 0 0 rgba(0, 0, 0, .15);
	box-shadow: 0 0 0 rgba(0, 0, 0, .15);
	margin: 20px auto;
}

.content-inner {
	padding: 0;
}
/* Header
----------------------------------------------- */
.header-outer {
	background: transparent none repeat-x scroll top left;
	_background-image: none;
	color: #ffffff;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-goog-ms-border-radius: 0;
	border-radius: 0;
}

.Header img,.Header #header-inner {
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-goog-ms-border-radius: 0;
	border-radius: 0;
}

.header-inner .Header .titlewrapper,.header-inner .Header .descriptionwrapper
	{
	padding-left: 0;
	padding-right: 0;
}

.Header h1 {
	font: normal normal 36px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
	text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.3);
}

.Header h1 a {
	color: #ffffff;
}

.Header .description {
	font-size: 130%;
}
/* Tabs
----------------------------------------------- */
.tabs-inner {
	margin: .5em 20px 0;
	padding: 0;
}

.tabs-inner .section {
	margin: 0;
}

.tabs-inner .widget ul {
	padding: 0;
	background: transparent none repeat scroll bottom;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-goog-ms-border-radius: 0;
	border-radius: 0;
}

.tabs-inner .widget li {
	border: none;
}

.tabs-inner .widget li a {
	display: inline-block;
	padding: .5em 1em;
	margin-right: .25em;
	color: #ffffff;
	font: normal normal 15px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
	-moz-border-radius: 10px 10px 0 0;
	-webkit-border-top-left-radius: 10px;
	-webkit-border-top-right-radius: 10px;
	-goog-ms-border-radius: 10px 10px 0 0;
	border-radius: 10px 10px 0 0;
	background: transparent
		url(//www.blogblog.com/1kt/transparent/black50.png) repeat scroll top
		left;
	border-right: 1px solid transparent;
}

.tabs-inner .widget li:first-child a {
	padding-left: 1.25em;
	-moz-border-radius-topleft: 10px;
	-moz-border-radius-bottomleft: 0;
	-webkit-border-top-left-radius: 10px;
	-webkit-border-bottom-left-radius: 0;
	-goog-ms-border-top-left-radius: 10px;
	-goog-ms-border-bottom-left-radius: 0;
	border-top-left-radius: 10px;
	border-bottom-left-radius: 0;
}

.tabs-inner .widget li.selected a,.tabs-inner .widget li a:hover {
	position: relative;
	z-index: 1;
	background: transparent
		url(//www.blogblog.com/1kt/transparent/white80.png) repeat scroll
		bottom;
	color: #336699;
	-moz-box-shadow: 0 0 3px rgba(0, 0, 0, .15);
	-webkit-box-shadow: 0 0 3px rgba(0, 0, 0, .15);
	-goog-ms-box-shadow: 0 0 3px rgba(0, 0, 0, .15);
	box-shadow: 0 0 3px rgba(0, 0, 0, .15);
}
/* Headings
----------------------------------------------- */
h2 {
	font: bold normal 13px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
	text-transform: uppercase;
	color: #888888;
	margin: .5em 0;
}
/* Main
----------------------------------------------- */
.main-outer {
	background: transparent
		url(//www.blogblog.com/1kt/transparent/white80.png) repeat scroll top
		left;
	-moz-border-radius: 20px 20px 0 0;
	-webkit-border-top-left-radius: 20px;
	-webkit-border-top-right-radius: 20px;
	-webkit-border-bottom-left-radius: 0;
	-webkit-border-bottom-right-radius: 0;
	-goog-ms-border-radius: 20px 20px 0 0;
	border-radius: 20px 20px 0 0;
	-moz-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	-webkit-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	-goog-ms-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
}

.main-inner {
	padding: 15px 20px 20px;
}

.main-inner .column-center-inner {
	padding: 0 0;
}

.main-inner .column-left-inner {
	padding-left: 0;
}

.main-inner .column-right-inner {
	padding-right: 0;
}
/* Posts
----------------------------------------------- */
h3.post-title {
	margin: 0;
	font: normal normal 18px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
}

.comments h4 {
	margin: 1em 0 0;
	font: normal normal 18px Arial, Tahoma, Helvetica, FreeSans, sans-serif;
}

.date-header span {
	color: #333333;
}

.post-outer {
	background-color: #ffffff;
	border: solid 1px #dddddd;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
	-goog-ms-border-radius: 5px;
	padding: 15px 20px;
	margin: 0 -20px 20px;
}

.post-body {
	line-height: 1.4;
	font-size: 110%;
	position: relative;
}

.post-header {
	margin: 0 0 1.5em;
	color: #999999;
	line-height: 1.6;
}

.post-footer {
	margin: .5em 0 0;
	color: #999999;
	line-height: 1.6;
}

#blog-pager {
	font-size: 140%
}

#comments .comment-author {
	padding-top: 1.5em;
	border-top: dashed 1px #ccc;
	border-top: dashed 1px rgba(128, 128, 128, .5);
	background-position: 0 1.5em;
}

#comments .comment-author:first-child {
	padding-top: 0;
	border-top: none;
}

.avatar-image-container {
	margin: .2em 0 0;
}
/* Comments
----------------------------------------------- */
.comments .comments-content .icon.blog-author {
	background-repeat: no-repeat;
	background-image:
		url(data:image/png;base64,);
}

.comments .comments-content .loadmore a {
	border-top: 1px solid #33aaff;
	border-bottom: 1px solid #33aaff;
}

.comments .continue {
	border-top: 2px solid #33aaff;
}
/* Widgets
----------------------------------------------- */
.widget ul,.widget #ArchiveList ul.flat {
	padding: 0;
	list-style: none;
}

.widget ul li,.widget #ArchiveList ul.flat li {
	border-top: dashed 1px #ccc;
	border-top: dashed 1px rgba(128, 128, 128, .5);
}

.widget ul li:first-child,.widget #ArchiveList ul.flat li:first-child {
	border-top: none;
}

.widget .post-body ul {
	list-style: disc;
}

.widget .post-body ul li {
	border: none;
}
/* Footer
----------------------------------------------- */
.footer-outer {
	color: #cccccc;
	background: transparent
		url(//www.blogblog.com/1kt/transparent/black50.png) repeat scroll top
		left;
	-moz-border-radius: 0 0 20px 20px;
	-webkit-border-top-left-radius: 0;
	-webkit-border-top-right-radius: 0;
	-webkit-border-bottom-left-radius: 20px;
	-webkit-border-bottom-right-radius: 20px;
	-goog-ms-border-radius: 0 0 20px 20px;
	border-radius: 0 0 20px 20px;
	-moz-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	-webkit-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	-goog-ms-box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
	box-shadow: 0 1px 3px rgba(0, 0, 0, .15);
}

.footer-inner {
	padding: 10px 20px 20px;
}

.footer-outer a {
	color: #99ccee;
}

.footer-outer a:visited {
	color: #77aaee;
}

.footer-outer a:hover {
	color: #33aaff;
}

.footer-outer .widget h2 {
	color: #aaaaaa;
}
/* Mobile
----------------------------------------------- */
html body.mobile {
	height: auto;
}

html body.mobile {
	min-height: 480px;
	background-size: 100% auto;
}

.mobile .body-fauxcolumn-outer {
	background: transparent none repeat scroll top left;
}

html .mobile .mobile-date-outer,html .mobile .blog-pager {
	border-bottom: none;
	background: transparent
		url(//www.blogblog.com/1kt/transparent/white80.png) repeat scroll top
		left;
	margin-bottom: 10px;
}

.mobile .date-outer {
	background: transparent
		url(//www.blogblog.com/1kt/transparent/white80.png) repeat scroll top
		left;
}

.mobile .header-outer,.mobile .main-outer,.mobile .post-outer,.mobile .footer-outer
	{
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	-goog-ms-border-radius: 0;
	border-radius: 0;
}

.mobile .content-outer,.mobile .main-outer,.mobile .post-outer {
	background: inherit;
	border: none;
}

.mobile .content-outer {
	font-size: 100%;
}

.mobile-link-button {
	background-color: #336699;
}

.mobile-link-button a:link,.mobile-link-button a:visited {
	color: #ffffff;
}

.mobile-index-contents {
	color: #333333;
}

.mobile .tabs-inner .PageList .widget-content {
	background: transparent
		url(//www.blogblog.com/1kt/transparent/white80.png) repeat scroll
		bottom;
	color: #336699;
}

.mobile .tabs-inner .PageList .widget-content .pagelist-arrow {
	border-left: 1px solid transparent;
}
-->
</style>
